create table if not exists public.thoughts (id uuid default gen_random_uuid() primary key, user_id uuid references auth.users(id) not null, content text not null, emotion integer not null default 50, mood_label text, intention text, created_at timestamptz default now(), updated_at timestamptz default now()); alter table public.thoughts enable row level security; create policy "Users can create their own thoughts" on public.thoughts for insert with check (auth.uid() = user_id); create policy "Users can view their own thoughts" on public.thoughts for select using (auth.uid() = user_id);
